const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [searchCity, setSearchCity] = useState("Mumbai");
  const [citySuggestions, setCitySuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const API_KEY = "461c465af010968d6dbc72a748b1a651";

  // Fetch the weather data based on the city
  const fetchWeather = async (city) => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
      alert("Unable to fetch weather data. Please try again.");
    }
  };

  // Handle form submission
  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchCity.trim()) {
      fetchWeather(searchCity.trim());
      setShowSuggestions(false);
    }
  };

  // Handle input change for city suggestions
  const handleCityChange = async (e) => {
    const inputValue = e.target.value.trim();
    setSearchCity(inputValue);

    if (!inputValue) {
      setShowSuggestions(false);
      return;
    }

    setShowSuggestions(true);
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/find?q=${inputValue}&appid=${API_KEY}`
      );

      if (response.data.list) {
        setCitySuggestions(response.data.list);
      } else {
        setCitySuggestions([]);
      }
    } catch (error) {
      console.error("Error fetching city suggestions:", error);
      setShowSuggestions(false);
    }
  };

  // Fetch initial weather data for default city
  useEffect(() => {
    fetchWeather(searchCity);
  }, []);

  return (
    <div className="weather-app">
      <h1>Weather App</h1>

      {weatherData && (
        <div className="weather-container">
          {/* Left Panel */}
          <div className="left-panel">
            <h2>{new Date().toLocaleDateString("en-US", { weekday: "long" })}</h2>
            <h3>{weatherData.city.name} - {weatherData.city.country}</h3>

            <div className="temperature">
              <img
                src={`https://openweathermap.org/img/wn/${weatherData.list[0].weather[0].icon}@2x.png`}
                alt={weatherData.list[0].weather[0].description}
                className="weather-icon"
              />
              <h1>{Math.round(weatherData.list[0].main.temp - 273.15)}Â°C</h1>
            </div>
            <p>{weatherData.list[0].weather[0].description}</p>
          </div>

          {/* Right Panel */}
          <div className="right-panel">
            <form onSubmit={handleSearch} className="search-form">
              <input
                type="text"
                placeholder="Enter city"
                className="search-input"
                value={searchCity}
                onChange={handleCityChange}
              />
              <button type="submit" className="search-button">
                Search
              </button>
            </form>

            {showSuggestions && citySuggestions.length > 0 && (
              <div className="suggestions-list">
                {citySuggestions.map((suggestion, index) => (
                  <div
                    key={index}
                    className="suggestion-item"
                    onClick={() => {
                      setSearchCity(suggestion.name);
                      fetchWeather(suggestion.name);
                      setShowSuggestions(false);
                    }}
                  >
                    {suggestion.name}, {suggestion.sys.country}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
