import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Weather.css"; // External CSS for styling

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState("Mumbai");
  const API_KEY = "461c465af010968d6dbc72a748b1a651";

  const fetchWeather = async (city) => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  useEffect(() => {
    fetchWeather(city);
  }, [city]);

  const handleSearch = (e) => {
    e.preventDefault();
    const searchCity = e.target.elements.city.value.trim();
    if (searchCity) {
      setCity(searchCity);
    }
  };

  return (
    <div className="weather-app">
      <h1>Weather App</h1>
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          name="city"
          placeholder="Enter city"
          className="search-input"
        />
        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      {weatherData && (
        <div className="weather-container">
          {/* Left Panel */}
          <div className="left-panel">
            <h2>{new Date().toLocaleDateString("en-US", { weekday: "long" })}</h2>
            <p>{new Date().toLocaleDateString()}</p>
            <h3>
              {weatherData.city.name} - {weatherData.city.country}
            </h3>
            <h1>
              {Math.round(weatherData.list[0].main.temp - 273.15)}Â°C
            </h1>
            <p>{weatherData.list[0].weather[0].description}</p>
          </div>
          {/* Right Panel */}
          <div className="right-panel">
            <div className="forecast">
              {weatherData.list.slice(0, 5).map((forecast, index) => (
                <div key={index} className="forecast-item">
                  <p>{new Date(forecast.dt * 1000).toLocaleDateString("en-US", { weekday: "short" })}</p>
                  <p>{Math.round(forecast.main.temp - 273.15)}Â°C</p>
                  <img
                    src={`http://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`}
                    alt={forecast.weather[0].description}
                  />
                </div>
              ))}
            </div>
            <div className="details">
              <p>UV Index: 8 (Very High)</p>
              <p>Humidity: {weatherData.list[0].main.humidity}%</p>
              <p>Wind Speed: {weatherData.list[0].wind.speed} km/h</p>
              <p>
                Population:{" "}
                {Intl.NumberFormat().format(weatherData.city.population || 0)}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
