import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css"; // External CSS for styling

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState("Mumbai");
  const [suggestions, setSuggestions] = useState([]);
  const API_KEY = "461c465af010968d6dbc72a748b1a651";

  // Fetch the weather data based on the city
  const fetchWeather = async (city) => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  // Handle form submission for city search
  const handleSearch = (e) => {
    e.preventDefault();
    const searchCity = e.target.elements.city.value.trim();
    if (searchCity) {
      setCity(searchCity); // Update city state
    }
  };

  // Handle city input change to fetch suggestions
  const handleCityChange = async (e) => {
    const searchCity = e.target.value.trim();
    if (searchCity) {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/find?q=${searchCity}&appid=${API_KEY}`
      );
      const citySuggestions = response.data.list.map((city) => city.name);
      setSuggestions(citySuggestions); // Set suggestions based on user input
    } else {
      setSuggestions([]); // Clear suggestions when input is empty
    }
  };

  // Fetch weather data when city changes
  useEffect(() => {
    fetchWeather(city);
  }, [city]);

  return (
    <div className="weather-app">
      <h1>Weather App</h1>
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          name="city"
          placeholder="Enter city"
          className="search-input"
          list="city-suggestions"
          onChange={handleCityChange} // Handle input change for suggestions
        />
        <datalist id="city-suggestions">
          {suggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
        <button type="submit" className="search-button">
          Search
        </button>
      </form>

      {weatherData && (
        <div className="weather-container">
          {/* Left Panel */}
          <div className="left-panel">
            <h2>{new Date().toLocaleDateString("en-US", { weekday: "long" })}</h2>
            <p>{new Date().toLocaleDateString("en-GB", {
              day: '2-digit',
              month: 'short',
              year: 'numeric'
            })}</p>
            <h3>
              {weatherData.city.name} - {weatherData.city.country}
            </h3>
            <div className="temperature">
              <img
                src={`https://openweathermap.org/img/wn/${weatherData.list[0].weather[0].icon}@2x.png`}
                alt={weatherData.list[0].weather[0].description}
                className="weather-icon"
              />
              <h1>{Math.round(weatherData.list[0].main.temp - 273.15)}°C</h1>
            </div>
            <p>{weatherData.list[0].weather[0].description}</p>
          </div>

          {/* Right Panel */}
          <div className="right-panel">
            <div className="forecast">
              {weatherData.list.slice(0, 5).map((forecast, index) => (
                <div key={index} className="forecast-item">
                  <p>
                    {new Date(forecast.dt * 1000).toLocaleDateString("en-US", {
                      weekday: "short",
                    })}
                  </p>
                  <p>{Math.round(forecast.main.temp - 273.15)}°C</p>
                  <img
                    src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`}
                    alt={forecast.weather[0].description}
                  />
                </div>
              ))}
            </div>
            <div className="details">
              <p>UV Index: 8 (Very High)</p>
              <p>Humidity: {weatherData.list[0].main.humidity}%</p>
              <p>Wind Speed: {weatherData.list[0].wind.speed} km/h</p>
              <p>
                Population:{" "}
                {Intl.NumberFormat().format(weatherData.city.population || 0)}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
